import React,{useState} from 'react';
import data from '../dummyData.json';


function CustomSearch(){
    const[userData, setuserData] = React.useState([]);
    const[location,setlocation] = React.useState('');
    React.useEffect(() =>{
        setuserData(data);
    })

    const handleSearch = () => {
        debugger
        const newData =
        userData
        .filter(val => val.location == (location == ''? val.location : location))
        .map((val) => {
            return(
            <div className='template' key={val.id}>
                <img src={val.image} alt="" />
                <h3>{val.title}</h3>
                <p className='price'>{val.price}</p>
            </div>  
            )
        }) 
    }

    return (
        <>
            <div>Search</div>
            <select className='form-select appearance-none
                block
                w-full
                px-3
                py-1.5
                text-base
                font-normal
                text-gray-700
                bg-white bg-clip-padding bg-no-repeat
                border border-solid border-gray-300
                rounded
                transition
                ease-in-out
                m-0
                focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none'
                onChange={(e) => setlocation(e.target.value)}>
                <option value=''>-Select-</option>
                <option value='New York USA'>New York, USA</option>
                <option value='California USA'>California, USA</option>
                <option value='Boston USA'>Boston, USA</option>
                <option value='Los Angeles USA'>Los Angeles, USA</option>
                <option value='Chicago USA'>Chicago, USA</option>
            </select>
            <button type="button" class="inline-block px-6 py-2.5 bg-blue-600 text-white font-medium text-xs leading-tight uppercase rounded shadow-md hover:bg-blue-700 hover:shadow-lg focus:bg-blue-700 focus:shadow-lg focus:outline-none focus:ring-0 active:bg-blue-800 active:shadow-lg transition duration-150 ease-in-out"
            onClick={handleSearch()}>Button</button>

        </>
    );
}

export default CustomSearch;
